/* eslint-disable func-names */
/* eslint-disable consistent-return */
/* eslint-disable react/prop-types */
import React from 'react';
import styled from 'styled-components';
import Slider from 'react-slick';

const TopstoriesContent = props => {
	// const Wrapper = styled.div`
	// 	display: flex;
	// 	flex-direction: row;
	// 	align-items: center;
	// 	padding: 10px;
	// 	background-color: #f2f2f2;
	// `;

	const TopStoriesItem = styled.div`
		max-width: 640px;
		min-width: 360px;
	`;

	const settings = {
		dots: true,
		infinite: true,
		speed: 500,
		slidesToShow: 1,
		slidesToScroll: 1,
	};

	console.log(props);

	const getTopStories = props.getData.data.map(items => {
		// const finalUrl = `https://www.rappler.com${items.url}`;
		const articleID = items.id;
		const imgPath = items.images[0].tn;
		const img = new URL(imgPath).pathname;
		return (
			<img key={articleID} src={`https://assets.rappler.com${img}`} alt='' />
		);
	});
	return (
		<Slider {...settings}>
			<TopStoriesItem>{getTopStories}</TopStoriesItem>
		</Slider>
	);
};

export default TopstoriesContent;
