/* eslint-disable func-names */
/* eslint-disable consistent-return */
/* eslint-disable react/prop-types */
import React from 'react';
import styled from 'styled-components';
import Slider from 'react-slick';

const TopstoriesContent = props => {
	const TopStoriesMainWrapper = styled.div`
		/* display: flex;
		flex-direction: row;
		align-items: center; */
		margin: 10px;
	`;

	const TopStoriesItemWrapper = styled.div`
		width: 100%;
		padding: 10px;
		margin: 10px 0;
		background-color: #f2f2f2;
	`;

	const TopStoriesImageWrapper = styled.div`
		max-width: 640px;
		min-width: 360px;
	`;

	const TopStoriesImage = styled.img`
		width: 100%;
	`;

	const settings = {
		dots: true,
		infinite: true,
		speed: 500,
		slidesToShow: 1,
		slidesToScroll: 1,
	};

	const getTopStories = props.getData.data.map(items => {
		// const finalUrl = `https://www.rappler.com${items.url}`;
		const articleID = items.id;
		const imgPath = items.images[0].tn;
		const img = new URL(imgPath).pathname;
		return (
			<div key={articleID}>
				<div>
					<img src={`https://assets.rappler.com${img}`} alt='' />
				</div>
			</div>
		);
	});
	return (
		<Slider {...settings}>
			{/* {getTopStories} */}
			<div>
				<h3>1</h3>
			</div>
			<div>
				<h3>2</h3>
			</div>
			<div>
				<h3>3</h3>
			</div>
			<div>
				<h3>4</h3>
			</div>
			<div>
				<h3>5</h3>
			</div>
			<div>
				<h3>6</h3>
			</div>
		</Slider>
	);
};

export default TopstoriesContent;
